#include <iostream>
using namespace std;

int insertionsort(int *arr, int n) {
    int count = 0;
    for (int i = 1; i < n; i++) {
        int key = arr[i];
        int j = i - 1;
        if (arr[j]<=key){
            count++;
        }

        while (j >= 0 && arr[j] > key) {
            count+=1;
            arr[j + 1] = arr[j];
            j = j - 1;
        }

        
        arr[j + 1] = key;
    }
    return count;
}

void printarr(int arr[],int n){
    cout<<"sorted array :- "<<endl;
    for (int i = 0; i < n; i++)
    {
        cout<<arr[i]<<" ";
    }
    cout<<endl;
    
}

int main(int argc, char const *argv[]) {

int arr[5]={3,4,5,1,2};

    cout <<"Number of comparisons :- "<<insertionsort(arr, 5);
    cout<<endl;
    printarr(arr,5);
    return 0;
}

